import React from 'react';
import {Text, View, StyleSheet } from '@react-pdf/renderer';

const styles = StyleSheet.create({
    headerContainer: {
        marginTop: 36
    },
    billTo: {
        marginTop: 65,
        paddingBottom: 20,
       
    },
    heading : {
        fontWeight : 'bold',
        paddingBottom: 20,
    },
    
    to : {
        paddingBottom: 20,
    }

  });


  const BillTo = ({flag}) => (
    <View style={styles.headerContainer}>
        <Text style={styles.billTo}>Tom Schlepp - Schäringerstraße 3 - 80634 München</Text>

        

     
        <Text style={styles.heading}>Disciples München-Haar</Text>
        <Text>Sarah Enstone</Text>
        <Text>Keferloherstraße 24</Text>
        <Text style={styles.to}>85540 Haar</Text>


        <Text>{flag === 'ID' ? 'DATE: 12.04.2022' : flag === 'DOM' ? 'DATE: 08.11.1988' : 'DATE: 05.08.2016'} </Text>

        <Text>Subject: Staff availibility at [CALENDARWEEK]/[YEAR]</Text>

        <Text>Dear Disciples München-Haar,</Text>

        <Text>Hearby I inform you about the availibity of our staff by location and nationality for the current week.</Text>

        <Text>If you are interested in getting in touch with us, feel free to contact us.</Text>

        <Text>[ADDRESSOR COMPANY NAME] is looking forward to support your event.</Text>

        <Text>With best regards,</Text>

        <Text>Tom Schlepp</Text>
    </View>
  );
  
  export default BillTo;
