import React from 'react';
import {Text, View, StyleSheet } from '@react-pdf/renderer';
import { useState } from 'react';




        const InvoiceTableHeader = (header) => {

            console.log(header.header[1])

             const head = Object.keys(header.header[1]);


  // console.log(typeof head)

            const borderColor = '#90e5fc'
const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        borderBottomColor: '#bff0fd',
        backgroundColor: '#bff0fd',
        borderBottomWidth: 1,
        alignItems: 'center',
        height: 24,
        textAlign: 'center',
        fontStyle: 'bold',
        flexGrow: 1,
    },
    description: {
        width: '60%',
        borderRightColor: borderColor,
        borderRightWidth: 1,
    },
    qty: {
        width: '10%',
        borderRightColor: borderColor,
        borderRightWidth: 1,
    },
    rate: {
        width: '15%',
        borderRightColor: borderColor,
        borderRightWidth: 1,
    },
    amount: {
        width: '15%'
    },
  });
            
            
            
            return (
              
                    
                    <View style={styles.container}>
                    <Text style={styles.description}>ID</Text> 
                    <Text style={styles.qty}>Last Name</Text>
                    <Text style={styles.rate}>First Name</Text>
                    <Text style={styles.amount}>Amount</Text>
                    </View>
            


             

            )
        }

  
  export default InvoiceTableHeader
