import React from 'react';
import { Text, View, StyleSheet } from '@react-pdf/renderer';
import { useState } from 'react';

const InvoiceTableHeader = ({ data, Head }) => {

    console.log(data)
    const head = Head[1];
    const borderColor = '#90e5fc'
    const styles = StyleSheet.create({
        container: {
            flexDirection: 'row',
            borderBottomColor: '#bff0fd',

            borderBottomWidth: 1,
            alignItems: 'center',
            height: 24,
            textAlign: 'center',
            fontStyle: 'bold',
            flexGrow: 1,
        },
        description: {
            width: '60%',
            borderRightColor: borderColor,
            borderRightWidth: 1,
        },
        qty: {
            width: '10%',
            borderRightColor: borderColor,
            borderRightWidth: 1,
        },
        rate: {
            width: '15%',
            borderRightColor: borderColor,
            borderRightWidth: 1,
        },
        amount: {
            width: '15%'
        },
    });



    return (
        <>
             {Object.entries(head)?.map(([k, ss]) =>
                <View style={styles.container}>
                    {console.log('=====>',ss)}
                    {Object.entries(ss)?.map(([k1, ss1]) => (
                        ss1 === '' ? <Text style={styles.qty}></Text> : <Text style={styles.qty}>{ss1}</Text>
                    ))
                    }

                </View>
            )}
            {Object.entries(data)?.map(([k, ss]) =>
                <View style={styles.container}>
                    {Object.entries(ss)?.map(([k1, ss1]) => (

                        ss1 === '' ? <Text style={styles.qty}></Text> : <Text style={styles.qty}>{ss1}</Text>

                    ))
                    }

                </View>
            )}

        </>
    )
}

export default InvoiceTableHeader
