import React from 'react';
import {Text, View, StyleSheet } from '@react-pdf/renderer';
import { useState } from 'react';




        const InvoiceTableHeader = ({data}) => {



           const head = data[1];

         

          


            

            



            const borderColor = '#90e5fc'
const styles = StyleSheet.create({
    container: {
        flexDirection: 'row',
        borderBottomColor: '#bff0fd',
        backgroundColor: '#bff0fd',
        borderBottomWidth: 1,
        alignItems: 'center',
        height: 24,
        textAlign: 'center',
        fontStyle: 'bold',
        flexGrow: 1,
    },
    description: {
        width: '60%',
        borderRightColor: borderColor,
        borderRightWidth: 1,
    },
    qty: {
        width: '10%',
        borderRightColor: borderColor,
        borderRightWidth: 1,
    },
    rate: {
        width: '15%',
        borderRightColor: borderColor,
        borderRightWidth: 1,
    },
    amount: {
        width: '15%'
    },
  });
            
            
            
            return (

<>


{head?.map((item) =>  
     <View style={styles.container}>
                    <Text style={styles.description}>{item.__EMPTY}</Text> 
                    <Text style={styles.description}>{item.__EMPTY_1}</Text> 
                    <Text style={styles.description}>{item.__EMPTY_2}</Text> 
                    <Text style={styles.description}>{item.__EMPTY_3}</Text> 
                    <Text style={styles.description}>{item.__EMPTY_4}</Text> 
                    <Text style={styles.description}>{item.__EMPTY_5}</Text> 
                    <Text style={styles.description}>dsjkdjkdj</Text> 

                    
                    </View>
            
)}




</>
                    
                   


             

            )
        }

  
  export default InvoiceTableHeader
